function ConfirmDelete(me)
{
	var intent = confirm('Do you really want to delete this record?');
	if(intent){window.location='areaincharge.php?xn=D&pid='+me;}
}

function validate_email(value1)
{
	var reg = /^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/;
	var address = document.getElementById(value1).value;
	if(reg.test(address) == false){return false;} else {return true;}
}

function check_value(me)
{
	var Amount = me.value.charAt(me.value.length-1);
	var Numfilter=/^[0-9.]+$/;
	var test_num = Numfilter.test(Amount);
	if(!test_num){
		alert("Please Enter Only numeric data!");
		if(me.value.length>0){
			if(me.id=="mobile")
				document.getElementById("mobile").value=document.getElementById("mobile").value.substring(0,me.value.length-1);
		}
		return false;
	} else {
		return true;
	}
}

function validate_aidata()
{
	if(document.getElementById("aiName").value==""){
		alert("* Please input area incharge name !");
		return false;
	}
	if(document.getElementById("state").value==0){
		alert("* Please select state name !");
		return false;
	}
	if(document.getElementById("userName").value==""){
		alert("* Please input user id !");
		return false;
	}
	if(document.getElementById("userPass").value==""){
		alert("* Please input password !");
		return false;
	}
	if(document.getElementById("confirmPass").value==""){
		alert("* Please input retype password !");
		return false;
	}
	if(document.getElementById("userPass").value!=document.getElementById("confirmPass").value){
		alert("* Password mismatching !");
		return false;
	}
	if(document.getElementById("mobile").value==""){
		alert("* Please input mobile no. !");
		return false;
	} else if(document.getElementById("mobile").value.length<10){
		alert("* Invalid mobile no. !");
		return false;
	}
	if(document.getElementById("eMail").value==""){
		alert("* Please input email !");
		return false;
	} else {
		var retval = validate_email('eMail');
		if(!retval){
			alert("* Invalid email address !");
			return false;
		}
	}
	return true;
}
