function validate_renewpc()
{
	if(document.getElementById("licenceNo").value==0){
		alert("* Please select Licence No. !!");
		document.getElementById("licenceNo").focus();
		return false;
	}
	if(document.getElementById("preIssueNo").value==""){
		alert("* Please input prefix for certificate no. !!");
		document.getElementById("preIssueNo").focus();
		return false;
	}
/*	if(document.getElementById("pcSerialNo").value==""){
		alert("* Please input serial/certificate no. of PC !!");
		document.getElementById("pcSerialNo").focus();
		return false;
	}*/
	if(document.getElementById("issueDate").value==""){
		alert("* Please input certificate renew date. !!");
		document.getElementById("issueDate").focus();
		return false;
	}
	if(document.getElementById("issueDate").value!=""){
		if(!checkdate(document.renewalpc.issueDate)){
			document.getElementById("issueDate").focus();
			return false;
		}
		var no_of_days2 = getDaysbetween2Dates(document.renewalpc.lastIssueDate, document.renewalpc.issueDate);
		if(no_of_days2 < 0){
			alert("* Sorry! PC Issue date wrongly selected.\nLast PC was issued on "+document.getElementById("lastIssueDate").value+".\nPlease correct and submit again.\n");
			document.getElementById("issueDate").focus();
			return false;
		}
	}
	var no_of_days1 = getDaysbetween2Dates(document.renewalpc.issueDate,document.renewalpc.licenceValidUpTo);
	if(no_of_days1 < 0){
		alert("* Sorry! Renewal date wrongly selected.\nPlease correct and submit again.\n");
		document.getElementById("issueDate").focus();
		return false;
	}
	if(document.getElementById("pcValidUpTo").value==""){
		alert("* Please input certificate validity date. !!");
		document.getElementById("pcValidUpTo").focus();
		return false;
	}
	if(document.getElementById("pcValidUpTo").value!=""){
		if(!checkdate(document.renewalpc.pcValidUpTo)){
			document.getElementById("pcValidUpTo").focus();
			return false;
		}
		var no_of_days2 = getDaysbetween2Dates(document.renewalpc.issueDate, document.renewalpc.pcValidUpTo);
		if(no_of_days2 < 0){
			alert("* Sorry! PC Validity date wrongly selected.\nPlease correct and submit again.\n");
			document.getElementById("pcValidUpTo").focus();
			return false;
		}
	}
}

function check_value(me)
{
	var Amount = me.value.charAt(me.value.length-1);
	var Numfilter=/^[0-9.]+$/;
	var test_num = Numfilter.test(Amount);
	if(!test_num){
		alert("Please Enter Only numeric data!");
		if(me.value.length>0){
			document.getElementById("pcSerialNo").value=document.getElementById("pcSerialNo").value.substring(0,me.value.length-1);
		}
		return false;
	} else {
		return true;
	}
}

function get_pc_valid_date()
{
	if(!checkdate(document.renewalpc.issueDate)){
		document.getElementById("issueDate").focus();
		return false;
	} else {
		var dayfield=document.renewalpc.issueDate.value.split("-")[0];
		var monthfield=document.renewalpc.issueDate.value.split("-")[1];
		var yearfield=document.renewalpc.issueDate.value.split("-")[2];
		yearfield = parseInt(yearfield)+1;
		document.renewalpc.pcValidUpTo.value = dayfield+"-"+monthfield+"-"+yearfield;
		return true;
	}
}

